import React, { useState, useEffect } from "react";
import ScrollReveal from "scrollreveal";
import AboutMe from './components/about-me/AboutMe'; 
import Skill from './components/skill/Skill';  
import Portfolio from './components/portfolio/Portfolio';  
import Experiences from './components/experiences/Experiences';  
import Copyright from './components/copyright/Copyright';  


function App() {
  // Estado para menu mobile
  const [menuOpen, setMenuOpen] = useState(false);

  // Estado para modal de idioma
  const [languageModalOpen, setLanguageModalOpen] = useState(false);

  // Estado para config.json
  const [config, setConfig] = useState(null);

  // Funções de menu
  const toggleMenu = () => setMenuOpen((v) => !v);
  const closeMenu = () => setMenuOpen(false);

  // Funções modal
  const toggleLanguageModal = () => setLanguageModalOpen((v) => !v);
  const closeLanguageModal = () => setLanguageModalOpen(false);

  // Função download CV
  const downloadCV = (language) => {
    const fileName =
      language === "pt"
        ? "cv_felipe_babel_pt.pdf"
        : "cv_felipe_babel_en.pdf";
    const filePath = `doc/${fileName}`;
    const link = document.createElement("a");
    link.href = filePath;
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    closeLanguageModal();
  };

  // Fecha modal ao rolar a página
  useEffect(() => {
    if (!languageModalOpen) return;

    const onScroll = () => closeLanguageModal();
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, [languageModalOpen]);

  // Fetch config.json e ScrollReveal
  useEffect(() => {
    fetch("/config.json")
      .then((res) => res.json())
      .then((data) => setConfig(data))
      .catch((err) => console.error("Erro ao carregar config.json", err));

    const sr = ScrollReveal({ distance: "40px", duration: 2500, reset: true });
    const sr20 = ScrollReveal({ distance: "10px", duration: 1500, reset: true });
    const sr80 = ScrollReveal({ distance: "80px", duration: 2500, reset: false });

    sr80.reveal(".nav-logo-home", { delay: 200, opacity: 0, origin: "left" });
    sr80.reveal(".nav-links-home", { delay: 200, origin: "right" });
    sr80.reveal("header .image", { delay: 200, origin: "bottom" });
    sr80.reveal(".btn-menu", { delay: 200, origin: "right" });

    sr.reveal(".name", { delay: 200, origin: "top" });
    sr20.reveal(".personal-projects", { delay: 200, origin: "right" });
    sr.reveal(".job", { delay: 200, origin: "right" });
    sr.reveal(".info-home", { delay: 200, origin: "left" });
    sr.reveal(".copyright", { delay: 200, origin: "left" });
    sr.reveal(".socials", { delay: 200, origin: "right" });
    sr.reveal(".aboutme .card", { delay: 200, origin: "right" });
    sr.reveal(".headeraboutme", { delay: 200, origin: "left" });
    sr.reveal(".headerskills", { delay: 200, origin: "right" });
    sr.reveal(".headerportfolio", { delay: 200, origin: "left" });
    sr.reveal(".headerexperiences", { delay: 200, origin: "right" });

    // Animação cards portfolio
    const cardsportfolio = document.querySelectorAll(".portfolio .card");
    if (window.matchMedia("(max-width: 600px)").matches) {
      cardsportfolio.forEach((card, index) => {
        const row = Math.floor(index / 1);
        const origin = row % 2 === 0 ? "left" : "right";
        sr.reveal(card, { delay: 200, origin });
      });
    } else if (window.matchMedia("(max-width: 900px)").matches) {
      cardsportfolio.forEach((card, index) => {
        const row = Math.floor(index / 2);
        const origin = row % 2 === 0 ? "left" : "right";
        sr.reveal(card, { delay: 200, origin });
      });
    } else {
      cardsportfolio.forEach((card, index) => {
        const row = Math.floor(index / 3);
        const origin = row % 2 === 0 ? "left" : "right";
        sr.reveal(card, { delay: 200, origin });
      });
    }

    // Animação cards skills
    const cardsskills = document.querySelectorAll(".skills .card");
    if (window.matchMedia("(max-width: 900px)").matches) {
      cardsskills.forEach((card, index) => {
        const row = Math.floor(index / 2);
        const origin = row % 2 === 0 ? "left" : "right";
        sr.reveal(card, { delay: 200, origin });
      });
    } else {
      cardsskills.forEach((card, index) => {
        const row = Math.floor(index / 4);
        const origin = row % 2 === 0 ? "left" : "right";
        sr.reveal(card, { delay: 200, origin });
      });
    }

    // animação cards experiences
    sr.reveal(".experiences .card:nth-child(odd)", { delay: 200, origin: "left" });
    sr.reveal(".experiences .card:nth-child(even)", { delay: 200, origin: "right" });
  }, []);

  // Fecha menu se clicar em link do menu mobile
  const handleMenuLinkClick = () => closeMenu();

  // Fecha menu ao clicar no overlay
  const handleOverlayClick = () => closeMenu();

  return (
    <>
      {/* Cabeçalho / Navegação */}
      <nav>
        <div className="nav-logo-home">
          <a href="#top">
            <img src="assets/images/logo_bold.jpg" alt="Logo Felipe Babel" />
          </a>
        </div>

        <ul className={`nav-links-home ${menuOpen ? "show-menu" : ""}`}>
          <li className="link" onClick={handleMenuLinkClick}>
            <a href="#top">Home</a>
          </li>
          <li className="link" onClick={handleMenuLinkClick}>
            <a href="#aboutme">About me</a>
          </li>
          <li className="link" onClick={handleMenuLinkClick}>
            <a href="#skills">Skills</a>
          </li>
          <li className="link" onClick={handleMenuLinkClick}>
            <a href="#portfolio">Portfolio</a>
          </li>
          <li className="link" onClick={handleMenuLinkClick}>
            <a href="#experiences">Experiences</a>
          </li>
        </ul>

        <button id="menu-icon" className="btn-menu" onClick={toggleMenu}>
          <i className="bx bx-menu"></i>
        </button>

        {/* mobile */}
        <div
          className={`overlay ${menuOpen ? "show-overlay" : "hide-overlay"}`}
          onClick={handleOverlayClick}
        ></div>
        <ul className={`nav-links-home-menu ${menuOpen ? "show-menu" : "hide-menu"}`}>
          <button id="btn-close" onClick={closeMenu}>
            <i className="ri-close-line"></i>
          </button>
          <li className="linkMenu" onClick={handleMenuLinkClick}>
            <a href="#top">Home</a>
          </li>
          <li className="linkMenu" onClick={handleMenuLinkClick}>
            <a href="#aboutme">About me</a>
          </li>
          <li className="linkMenu" onClick={handleMenuLinkClick}>
            <a href="#skills">Skills</a>
          </li>
          <li className="linkMenu" onClick={handleMenuLinkClick}>
            <a href="#portfolio">Portfolio</a>
          </li>
          <li className="linkMenu" onClick={handleMenuLinkClick}>
            <a href="#experiences">Experiences</a>
          </li>
        </ul>
      </nav>

      {/* Home */}
      <header className="container">
        <div className="content">
          <div className="home">
            <h1 className="name">Felipe Babel</h1>
            <h1 className="job">Back-End Software Engineer</h1>
            <div className="info-home">
              <p></p>
              <p>Bachelor's degree in Information Systems</p>
              <p>3 years of professional experience</p>
              <p id="variable_english">
                {config?.home?.certifieds?.[0] ? (
                  <a
                    href={config.home.certifieds[0].url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {config.home.certifieds[0].descriptions}
                  </a>
                ) : null}
              </p>
              <a>1x AWS Certified</a>
              <p></p>
            </div>
            <div className="column">
              <div id="empty-space" style={{ height: "1rem" }}></div>
              <div className="socials">
                <button className="btn" onClick={toggleLanguageModal}>
                  Download CV
                </button>
                <a
                  href="https://www.linkedin.com/in/felipebabel/"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <i className="ri-linkedin-line"></i>
                </a>
                <a href="https://github.com/felipebabel" target="_blank" rel="noopener noreferrer">
                  <i className="ri-github-line"></i>
                </a>
              </div>
            </div>
            <p></p>
          </div>
        </div>
        <div className="image" id="imageprofile">
          <img src="assets/images/profile.png" alt="Felipe Babel" />
        </div>
      </header>

      {/* Modal Download CV */}
      <div
        id="languageModal"
        className={`language-modal ${languageModalOpen ? "show" : ""}`}
      >
        <h3>Select Language</h3>
        <button className="btn" onClick={() => downloadCV("pt")}>
          Portuguese
        </button>
        <button className="btn" onClick={() => downloadCV("en")}>
          English
