      </div>
      <div
        id="languageModalOverlay"
        className={`language-modal-overlay ${
          languageModalOpen ? "show" : "hide-overlay"
        }`}
        onClick={toggleLanguageModal}
      ></div>

      <div><AboutMe/></div>
      <div><Skill/></div>
      <div><Portfolio/></div>
      <div><Experiences/></div>
      <div><Copyright/></div>
    </>
  );
}

