import React, { useState } from 'react';
import styles from './portfolio.module.css';

const projects = [
  {
    id: 1,
    title: 'Login Project',
    imgSrc: 'assets/gif/projects/email.gif',
    alt: 'Login Project',
    description: `Login Project
Sends emails with or without random PDF attachments using AWS SES. Built with Java 21, Spring Boot, Docker.`,
    githubLink: 'https://github.com/felipebabel/email-microservice',
  },
  {
    id: 2,
    title: 'F1 Project',
    imgSrc: 'assets/gif/projects/consul.gif',
    alt: 'F1 Project',
    description: `F1 Project
Java microservices architecture using Consul for service discovery and KV store, with JWT-based authentication and load-balanced communication across service instances.`,
    githubLink: 'https://github.com/felipebabel/consul',
  },
];

export default function Portfolio() {
  const [activeDesc, setActiveDesc] = useState(null);

  return (
    <section className={styles.container} id="portfolio">
      <div id="empty-space" style={{ height: '3rem' }}></div>
      <h2 className={styles.headerportfolio}>PORTFOLIO</h2>
      <p className={styles.personalProjects}>My personal projects at Github.</p>
      <div className={styles.portfolio}>
        {projects.map((project) => (
          <div
            key={project.id}
            className={styles.card}
            onMouseEnter={() => setActiveDesc(project.id)}
            onMouseLeave={() => setActiveDesc(null)}
          >
            <div className={styles.image}>
              <img
                src={project.imgSrc}
                loading="lazy"
                alt={project.alt}
              />
              <div
                className={`${styles.description} ${
                  activeDesc === project.id ? styles.descriptionActive : ''
                }`}
              >
                <p>{project.description.split('\n').map((line, idx) => (
                  <React.Fragment key={idx}>
                    {line}
                    <br />
                  </React.Fragment>
                ))}</p>
              </div>
            </div>
            <div className={styles.linkGithub}>
              <a
                href={project.githubLink}
                target="_blank"
                rel="noopener noreferrer"
              >
                <i className="ri-github-line"></i>
              </a>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
